import React, { Component } from 'react';
import { StyleSheet, View, Text, Image, TextInput, TouchableOpacity} from 'react-native';
import { db, auth } from '../firebase/config';
import firebase from 'firebase';
import { ActivityIndicator, FlatList } from 'react-native-web';

class Comments extends Component{
    constructor (props) {
        super(props)
        this.state={
            comments: '',
            id: this.props.route.params.idPost,
            post: [],
            loading1: true,
        }
    }

    componentDidMount(){
    db.collection('posts').doc(this.state.id).onSnapshot(
        doc => {
                this.setState({
                post: doc.data(),
                loading1:false
            })
            db.collection('users').where('owner', '==', this.props.route.params.mail).onSnapshot(
                docs =>{
                    let usuario = [];
                    docs.forEach (doc => {
                        usuario.push({
                            id: doc.id,
                            data: doc.data()
                        })
                    this.setState({
                        username: usuario[0].data.username,
                        bio:usuario[0].data.bio,
                        mail:usuario[0].data.owner,
                        foto:usuario[0].data.foto,
                        loading2: false
                    }, () => console.log(this.state))
                    })
                }
            )
        }
    )
}

sendComment(comments) {
    db.collection('posts')
        .doc(this.state.id) 
        .update({
            comments: firebase.firestore.FieldValue.arrayUnion({
                owner: auth.currentUser.email,
                comments: comments,
                createdAt: Date.now()
            })
        })
        .then(() => {
            this.setState({
                comments: ''
            })
        })
}

render() {
    console.log(this.state.post)
    return (
        <View>
            {this.state.loading1 === true ? <ActivityIndicator size='large' color='green' /> :
            
            this.state.post.comments.length === 0 ? <Text>no hay comentarios</Text> 
            : 
            <View> 
                <View style={styles.containerPost}>
                   <Image style={styles.profilePhoto}/> 
                   <Text style={styles.username}></Text>
                   <Image style={styles.postPhoto} source={{uri:this.state.post.photo}} resizeMode='container' /> 
                </View>
                <FlatList data={this.state.post.comments} keyExtractor={unComentario=> unComentario.createdAt.toString()} renderItem={({ item }) => <> <Text> {item.owner} </Text> <Text>{item.comments}</Text> </> } />
            </View>
         }
                  <View>
                    <TextInput
                        placeholder='Agregar Comentario'
                        keyboardType='default'
                        onChangeText={text => this.setState({ comments: text })}
                        value={this.state.comments}
                    />
                    <TouchableOpacity onPress={() => this.sendComment(this.state.comments)}>
                        <Text>Comentar</Text>
                    </TouchableOpacity>
                </View>
        </View>
    )

}
}

const styles = StyleSheet.create({
    containerPost: {
      backgroundColor:'red',
      alignItems:'center',
      height:'40vh'
    },
    profilePhoto:{
        width:60,
        height:60
    },
    username:{
        fontSize:10
    },
    postPhoto:{
       height:'50%',
       width:'100%' 
    }

  });
  

export default Comments
