import React, { Component } from 'react';
import { View, Text, Image, TextInput, TouchableOpacity} from 'react-native';
import { db, auth } from '../firebase/config';
import { StyleSheet } from 'react-native-web';
import firebase from 'firebase';


class UnPost extends Component {
    constructor(props){
        super(props)
        this.state = {
            usuario: [],
            username: '',
            nroDeLikes: this.props.post.data.likes.length,
            userLike:false,
            fotoUsuario:'Fede'
        }
    }

    componentDidMount(){
        //console.log(this.props.post.id);
        if(this.props.post.data.likes.includes(auth.currentUser.email)){ 
            this.setState({
                userLike:true
            })
        }
        db.collection('users').where('owner', '==', this.props.post.data.owner).onSnapshot(
            docs => {
                let unUsuario = []
  //              console.log(docs);
                docs.forEach(doc => {
                    unUsuario.push({
                        id:doc.id,
                        data:doc.data()
                    })
                })
                this.setState({
                    usuario: unUsuario[0],
                    username: unUsuario[0].data.username,
                    fotoUsuario: unUsuario[0].data.foto
                })
                console.log(this.state.fotoUsuario,'Rada');
            })
    }

    like(){
        db.collection('posts')
            .doc(this.props.post.id) 
            .update({
                likes: firebase.firestore.FieldValue.arrayUnion(auth.currentUser.email) 
            })
            .then(()=> this.setState({
                nroDeLikes: this.state.nroDeLikes +1,
                userLike: true, 
                })
            )
            .catch(e=>console.log(e))
    }

    unlike(){
            db.collection('posts')
            .doc(this.props.post.id) 
            .update({
                likes: firebase.firestore.FieldValue.arrayUnion(auth.currentUser.email) 
            })
            .then(()=> this.setState({
                nroDeLikes: this.state.nroDeLikes -1,
                userLike: false, 
                })
            )
            .catch(e=>console.log(e))
    }

    borrarPost(){
        let bool = confirm('desea borrar el post?')
        {bool ? db.collection("posts").doc(this.props.post.id).delete()
        : console.log('se arrepintio');}
        
    }

    render(){
        console.log(this.state.fotoUsuario,'Fede');
        return(
                <>
                    <Image 
                        style={styles.image}
                        source={{uri:this.props.post.data.photo}}
                        resizeMode='auto'
                    />
                    <View  style={styles.caja}>
                    
                    <TouchableOpacity onPress={()=> this.props.navigation.navigate('Profile', {mail: this.state.usuario.data.owner})}>
                        <Image 
                            style={styles.profile}
                            source={{uri:this.state.fotoUsuario}}
                            resizeMode='auto' 
                        />
                    </TouchableOpacity>

                    {/* <Text>{this.props.post.data.text}</Text>*/}
                    <Text> Cantidad de Likes: {this.state.nroDeLikes} </Text>
                    
                    { this.state.userLike ? 
                    <TouchableOpacity onPress={ ()=> this.unlike() }>
                        <Text>Unlike</Text>
                    </TouchableOpacity>
                    :
                    <TouchableOpacity onPress={ ()=> this.like() }>
                        <Text>Like</Text>
                    </TouchableOpacity>
                    }

                    {auth.currentUser.email == this.props.post.data.owner ?
                    <TouchableOpacity onPress={() => this.borrarPost()}>
                        <Text>Borrar post</Text>   
                    </TouchableOpacity> 
                    : ''
                    }
                    
                    <TouchableOpacity>
                        <Text onPress={ () => this.props.navigation.navigate('Comments',{idPost: this.props.post.id})} >comentarios</Text>
                    </TouchableOpacity>

                    </View>                    
                    
                </>
                
            )
        }
    }
//<Text>{this.state.usuario[0].data.bio}</Text>   esto va arriba de la imagen, tiene que mostrar el nombre de usauario que subio el post, y cuando lo tocas redirigir a la view de ver perfil de ese user
    const styles = StyleSheet.create({
        image: {
            height: 500,
            width:350,
            alignSelf:'center',
            borderBottomRightRadius: 8,
            borderBottomLeftRadius: 8,
            borderTopRightRadius: 8,
            borderTopLeftRadius: 8,
            margin:10
        },
        caja: {
            flex:1,
            flexDirection: 'row',
            backgroundColor:'red',
            width:250,
            alignSelf:'center',
            borderBottomRightRadius: 5,
            borderBottomLeftRadius: 5,
            borderTopRightRadius: 5,
            borderTopLeftRadius: 5
        },
        profile: {
            height:70,
            width:70,
            borderBottomRightRadius: 50,
            borderBottomLeftRadius: 50,
            borderTopRightRadius: 50,
            borderTopLeftRadius: 50,
        }
    })
    

export default UnPost;
