import React, { Component } from 'react';
import { View, Text, TextInput, TouchableOpacity} from 'react-native';
import { ActivityIndicator, FlatList } from 'react-native-web';
import { db, auth } from '../firebase/config';

import UnPost from '../Components/UnPost';

class Profile extends Component {
    constructor(){
        super()
        this.state = {
            posteos: [],
            loading: true
        }
    }

    componentDidMount(){
        
    }

    obtenerPosteos(){

    }

    logout() {
        auth.signOut()
        this.props.navigation.navigate('Login')
    }
    
    render(){
        return(
            
            <View>
                <Text>Estas en la Profile</Text>
                {this.state.loading ? <ActivityIndicator size='large' color='green' /> : 
                <FlatList
                    data={this.state.posteos}
                    keyExtractor={item => item.id.toString()}
                    renderItem={({item}) => <UnPost post={item} />}
                />
                
                }
                <TouchableOpacity onPress={() => this.logout()}>
                    <Text>Logout</Text>   
                </TouchableOpacity>

            </View>
        )
    }
    
}


export default Profile
