import React, { Component } from 'react';
import { View, Text, TextInput, TouchableOpacity,StyleSheet,Image} from 'react-native';
import { ActivityIndicator, FlatList } from 'react-native-web';
import { db, auth } from '../firebase/config';

import PostProfile from '../Components/postsProfile';

class MyProfile extends Component {
    constructor(){
        super()
        this.state = {
            posteos: [],
            usuario: [],
            username: '',
            bio:'',
            foto:'',
            mail:'',
            loading1: true,
            loading2: true
        }
    }

    componentDidMount(){
        this.obtenerPosteos();
        this.obtenerUsuario()
    }

    obtenerPosteos(){
        db.collection('posts').where('owner', '==', auth.currentUser.email).orderBy('createdAt', 'desc').onSnapshot(
            docs =>{
                let posts = [];
                docs.forEach (doc => {
                    posts.push({
                        id: doc.id,
                        data: doc.data()
                    })
                this.setState({
                    posteos: posts,
                    loading1: false
                })
                })
            }
        )
    }

    obtenerUsuario(){
        db.collection('users').where('owner', '==', auth.currentUser.email).onSnapshot(
            docs =>{
                let usuario = [];
                docs.forEach (doc => {
                    usuario.push({
                        id: doc.id,
                        data: doc.data()
                    })
                this.setState({
                    usuario: usuario[0],
                    username: usuario[0].data.username,
                    bio:usuario[0].data.bio,
                    mail:usuario[0].data.owner,
                    foto:usuario[0].data.foto,
                    loading2: false
                })
                })
            }
        )
    }

    logout() {
        auth.signOut()
        this.props.navigation.navigate('Login')
    }

    borrarCuenta(){
        console.log(this.state.usuario.id);
        db.collection("users").doc(this.state.usuario.id).delete()
        .then(()=> {
            auth.currentUser.delete()
        })
        .then(()=> {
            this.props.navigation.navigate('Register')
        
        })
        .catch((err) => 'pifiaste algo, el error es: ' + err)
    }

    borrarPost(){
        let bool = confirm('desea borrar el post?')
        if (bool){
            db.collection("posts").doc(id).delete()
        }
    }

    render(){
        return(
            <View style={styles.scroll}> 
               
                
                    <View style={styles.upContainer}>
                        <Text style={styles.titulo}>My Profile</Text>
                        <TouchableOpacity onPress={() => this.borrarCuenta()}>
                            <Text style={styles.text}>Borrar cuenta</Text>   
                        </TouchableOpacity>
                    </View>

                    <View style={styles.infoContainer}>
                        <Image 
                        style={styles.img}
                        source={{uri: this.state.foto}}
                        resizeMode='cover'
                        /> 

                        <Text>{this.state.username}</Text>
                        <Text>{this.state.mail}</Text>
                        <Text>Posts: {this.state.posteos.length}</Text>
                        <Text>{this.state.bio}</Text>
                        <TouchableOpacity onPress={() => this.logout()}>
                            <Text>Logout</Text>   
                        </TouchableOpacity>
                    </View>

                    {this.state.loading1 && this.state.loading2 ? <ActivityIndicator size='large' color='green' /> : 
                    <FlatList
                        style={styles.list}
                        data={this.state.posteos}
                        keyExtractor={item => item.id.toString()}
                        renderItem={({item}) => <PostProfile post={item} borrar={this.borrarPost()}/>}
                    />
                    }
            </View>
        )
    }
    
}

const styles = StyleSheet.create({
    infoContainer:{
        flexDirection:'column',
        alignItems:'center',
        height:'30vh'
    },
    img:{
        borderBottomRightRadius: 50,
        borderBottomLeftRadius: 50,
        borderTopRightRadius: 50,
        borderTopLeftRadius: 50,
        height:85,
        width:85
    },
    titulo:{
        fontWeight:'bold',
        fontFamily: "'Helvetica', 'Arial', sans-serif;",
        fontSize:40
      },
      upContainer:{
        flexDirection:'row',
        justifyContent:'space-around'
      },
      text:{

      },
      scroll:{
        flex:1,
      },
      list:{
        flex:1,
        flexWrap:'wrap'
      }
}) 


export default MyProfile
