import React, { Component } from 'react';
import { View, Text, Image, TextInput, TouchableOpacity,ImageBackground} from 'react-native';
import { db, auth } from '../firebase/config';
import { StyleSheet } from 'react-native-web';
import firebase from 'firebase';


class UnComentario extends Component {
    constructor(props){
        super(props)
        this.state = {
            usuario: [],
            username: '',
            nroDeLikes: this.props.post.data.likes.length,
            userLike:false,
            fotoUsuario:'',
            mailUsuario:''
        }
    }

    componentDidMount(){
        if(this.props.post.data.likes.includes(auth.currentUser.email)){ 
            this.setState({
                userLike:true
            })
        }
        db.collection('users').where('owner', '==', this.props.post.data.owner).onSnapshot(
            docs => {
                let unUsuario = []
  //              console.log(docs);
                docs.forEach(doc => {
                    unUsuario.push({
                        id:doc.id,
                        data:doc.data()
                    })
                })
                this.setState({
                    usuario: unUsuario[0],
                    username: unUsuario[0].data.username,
                    fotoUsuario: unUsuario[0].data.foto,
                    mailUsuario: unUsuario[0].data.owner
                })
            })
    }

    like(){
        db.collection('posts')
            .doc(this.props.post.id) 
            .update({
                likes: firebase.firestore.FieldValue.arrayUnion(auth.currentUser.email) 
            })
            .then(()=> this.setState({
                nroDeLikes: this.state.nroDeLikes +1,
                userLike: true, 
                })
            )
            .catch(e=>console.log(e))
    }

    unlike(){
            db.collection('posts')
            .doc(this.props.post.id) 
            .update({
                likes: firebase.firestore.FieldValue.arrayRemove(auth.currentUser.email) 
            })
            .then(()=> this.setState({
                nroDeLikes: this.state.nroDeLikes -1,
                userLike: false, 
                })
            )
            .catch(e=>console.log(e))
    }

    render(){
        return(
                <View>
                                  
                </View>
            )
        }
    }
const styles = StyleSheet.create({
       
    })
    

export default UnComentario;
