import React, { Component } from 'react';
import { View, Text, Image, TextInput, TouchableOpacity} from 'react-native';
import { db, auth } from '../firebase/config';
import firebase from 'firebase';
import { StyleSheet,ActivityIndicator, FlatList } from 'react-native-web';
import UnComentario from '../Components/UnComentario'

class Comments extends Component{
    constructor (props) {
        super(props)
        this.state={
            comments: '',
            id: this.props.route.params.idPost,
            post: [],
            loading1: true,
            username:'',
            fotoUsuario:'',
            bioUsuario:''
        }
    }
    
    componentDidMount(){ 
    db.collection('posts').doc(this.state.id).onSnapshot(
        doc => {
                this.setState({
                post: doc.data(),
                loading1:false
            })
        })
    db.collection('users').where('owner', '==', this.props.route.params.mail).onSnapshot(
            docs =>{
                let usuario = [];
                docs.forEach (doc => {
                    usuario.push({
                        id: doc.id,
                        data: doc.data()
                    })
                this.setState({
                    username: usuario[0].data.username,
                    fotoUsuario:usuario[0].data.foto,
                    bioUsuario:usuario[0].data.bio
                })
                })
            }
        )
}

sendComment(comments) {
    db.collection('posts')
        .doc(this.state.id) 
        .update({
            comments: firebase.firestore.FieldValue.arrayUnion({
                owner: auth.currentUser.email,
                comments: comments,
                createdAt: Date.now()
            })
        })
        .then(() => {
            this.setState({
                comments: ''
            })
        })
}

render() {
    return (
        <View>
            {this.state.loading1 === true ? <ActivityIndicator size='large' color='green' /> :
            
            this.state.post.comments.length === 0 ? <Text>no hay comentarios</Text> 
            : 
            <View> 
                <View style={styles.containerPost}>
                   <Image style={styles.profilePhoto} source={{uri:this.state.fotoUsuario}} resizeMode='auto'/> 
                   <Text style={styles.username}>{this.state.username}</Text>
                   <Image style={styles.postPhoto} source={{uri:this.state.post.photo}} resizeMode='contain' /> 
                   <Text style={styles.bio}>{this.state.bioUsuario}</Text>
                </View>
                <FlatList data={this.state.post.comments} keyExtractor={unComentario=> unComentario.createdAt.toString()} renderItem={({ item }) =>  <UnComentario comment={item}/>} />
            </View>
         }
                  <View style={styles.containerAdd}>
                    <TextInput
                        placeholder='Agregar Comentario'
                        keyboardType='default'
                        onChangeText={text => this.setState({ comments: text })}
                        value={this.state.comments}
                    />
                    <TouchableOpacity onPress={() => this.sendComment(this.state.comments)}>
                        <Text>Comentar</Text>
                    </TouchableOpacity>
                </View>
        </View>
    )

}
}

const styles = StyleSheet.create({
    containerPost: {
      alignItems:'center',
      height:'50vh',
      marginTop:30
    },
    profilePhoto:{
        width:60,
        height:60,
        borderBottomRightRadius: 50,
        borderBottomLeftRadius: 50,
        borderTopRightRadius: 50,
        borderTopLeftRadius: 50,
    },
    username:{
        fontSize:20,
        marginTop:10
    },
    postPhoto:{
       marginTop:10,
       height:'70%',
       width:'100%' 
    },
    bio:{
        alignSelf:'flex-start',
        fontSize:20,
        marginTop:2,
        marginLeft:4
    },
    containerAdd:{
        flexDirection:'row',
        backgroundColor:'black',
        height:60
    }

  });
  

export default Comments
